<template>

  <div id="container" @dblclick="snapshot">
    <v-toolbar color="blue">
      <v-toolbar-title class="white--text">AI eyes</v-toolbar-title>
      <v-spacer></v-spacer>
    </v-toolbar>
    <video id="video"></video>
    <div id="cards">
      <v-card id="card" v-for="item in cards">
        {{item}}
      </v-card>
    </div>
    <v-btn fab dark large color="purple" @click="snapshot1" id="play_btn1">

     <img src="data:image/png;base64,"></img>
    </v-btn>
        <v-btn fab dark large color="purple" @click="snapshot2" id="play_btn2">
     <img src="data:image/png;base64,"></img>
    </v-btn>
        <v-btn fab dark large color="purple" @click="snapshot3" id="play_btn3">
        <img src="data:image/png;base64,"></img>
    </v-btn>
  </div>
</template>

<script>
import api from '../api';

export default {
  name: 'HelloWorld',
  data() {
    return {
      translated: '',
      cards: [],
      readingList: [],
      curRead: 0,
      playing: false,
    };
  },
  mounted() {
    // Grab elements, create settings, etc.
    const video = document.getElementById('video');

    // Get access to the camera!
    if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
      // Not adding `{ audio: true }` since we only want video now
      navigator.mediaDevices.getUserMedia({ video:true  }).then((stream) => {
        video.src = window.URL.createObjectURL(stream);
        video.play();
      });

    }
  },
  methods: {
    snapshot1(){
      this.snapshot('w');
    },
    snapshot2(){
      this.snapshot('d');
    },
    snapshot3(){
      this.snapshot('c');
    },
    snapshot(action) {
      const canvas = (this.canvas = document.createElement('canvas'));
      const context = canvas.getContext('2d');
      const video = document.getElementById('video');
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      context.drawImage(video, 0, 0, video.videoWidth, video.videoHeight);

      this.playing = false;
      this.curRead = 0;
      this.readingList = [];
      this.imageCaption(action);
    },

    playCurAudio() {
      const audio = document.createElement('audio');
      if (this.curRead >= this.readingList.length || this.playing) {
        return;
      }
      this.playing = true;
      audio.src = this.readingList[this.curRead++];
      audio.play();

      audio.onended = () => {
        if (this.curRead < this.readingList.length || !this.playing) {
          audio.src = this.readingList[this.curRead++];
          audio.play();
        } else {
          this.playing = false;
        }
      };
    },

    translate(text) {
      api.translate(text, 'en-zh').then((res) => {
        this.translated = res.data.translation[0];
        this.cards.push(res.data.translation[0]);
        this.readingList.push(res.data.tSpeakUrl);
        this.playCurAudio();
      }).catch((err) => {
        alert(err);
      });
    },

    imageCaption(action) {
      this.canvas.toBlob(
        (data) => {
          api.imageCaption(data,action).then((res) => {
            this.cards.push(res.data[action]);
            this.translate(res.data[action]);
            setTimeout(() => {
              document.querySelector("#cards").scrollTo(0,10000000000);
            },100);
            console.log(res.data);
          });
        },
        'image/jpeg',
        1,
      );
    }
  }
};
</script>

